describe(`checkVersionIsValid`, () => {
    const versionTests = [
        {
            name: "higher_major",
            currentVersion: "100.0.0",
            expectError: true,
        },
        {
            name: "lower_major",
            currentVersion: "1.0.0",
            expectError: true,
        },
        {
            name: "higher_minor",
            currentVersion: "v2.22.0",
            expectError: false,
        },
        {
            name: "lower_minor",
            currentVersion: "v2.1.0",
            expectError: true,
        },
        {
            name: "equal_minor_higher_patch",
            currentVersion: "v2.21.2",
            expectError: false,
        },
        {
            name: "equal_minor_equal_patch",
            currentVersion: "v2.21.1",
            expectError: false,
        },
        {
            name: "equal_minor_lower_patch",
            currentVersion: "v2.21.0",
            expectError: true,
        },
        {
            name: "equal_minor_equal_patch_prerelease",
            // Note that prerelease < release so this case will error
            currentVersion: "v2.21.1-alpha.1234",
            expectError: true,
        },
    ];
    const minVersion = new semver.SemVer("v2.21.1");
    versionTests.forEach(test => {
        it(`validates ${test.currentVersion}`, () => {
            const currentVersion = new semver.SemVer(test.currentVersion);
            if (test.expectError) {
                if (minVersion.major < currentVersion.major) {
                    assert.throws(() => automation_1.validatePulumiVersion(minVersion, currentVersion), /Major version mismatch./);
                }
                else {
                    assert.throws(() => automation_1.validatePulumiVersion(minVersion, currentVersion), /Minimum version requirement failed./);
                }
            }
            else {
                assert.doesNotThrow(() => automation_1.validatePulumiVersion(minVersion, currentVersion));
            }
        });
    });
});
const getTestSuffix = () => {
    return Math.floor(100000 + Math.random() * 900000);
};
const normalizeConfigKey = (key, projectName) => {
    const parts = key.split(":");
    if (parts.length < 2) {
        return `${projectName}:${key}`;
    }
    return "";
};
